morse_hash = {"a" => ".- ", "b" => "-... ", "c" => "-.-. ", "d" => "-.. ", "e" => ". ", "f" => "..-. ", "g" => "--. ", "h" => ".... ", "i" => ".. ", "j" => ".--- ", "k" => "-.-. ", "l" => ".-.. ", "m" => "-- ", "n" => "-. ", "o" => "--- ", "p" => ".--. ", "q" => "--.- ", "r" => ".-. ", "s" => "... ", "t" => "- ", "u" => "..- ", "v" => "...- ", "w" => ".-- ", "x" => "-..- ", "y" => "-.-- ", "z" => "--.. ", "1" => ".---- ", "2" => "..--- ", "3" => "...-- ", "4" => "....- ", "5" => "..... ", "6" => "-.... ", "7" => "--... ", "8" => "---.. ", "9" => "----. ", "0" => "----- ", " " => "  ", "." => "   "} 

puts "Enter a message to be translated into morse code:"
user_input = gets.chomp.downcase

(0..user_input.length).each do |letter|
    print morse_hash[user_input[letter]]